import React, { useEffect, useReducer } from "react";

const ACTIONS = ["View", "Reply"];

const formatAsCurrency = (int) => {
    // TODO
    if (typeof int === 'number') {
        return '$' + int.toLocaleString();
    }
    return int;
};

const Listing = (props) => {
    // TODO
    // This should be the component which renders an individual listing to the page
    const { title, description, imgUrl, location, price } = props;

    const handleAction = (action, title) => {
        console.log('action', action, title)
    }

    return (
        <div className="listing">
            <h3 className="listing__title">{title}</h3>
            <div className="listing__subtitle">
                <div className="listing__price">{formatAsCurrency(price)}</div>
                <div className="listing__location">{location}</div>
            </div>

            {
                imgUrl &&
                <div className="listing__img-wrap">
                    <img alt={title} src={imgUrl} className="listing__img"/>
                </div>
            }

            { description &&
                <p className="listing__description">
                    { description }
                </p>
            }
            <footer className="listing__footer">
                {
                    ACTIONS.map(action => <button key={action} className="btn" onClick={() => handleAction(action, title)}>{action}</button>)
                }
            </footer>
        </div>
    );
};

const INITIAL_STATE = {
    loading: true,
    error: '',
    data: [],
}

const reducer = (state, action) => {
    switch (action.type) {
        case 'SUCCESS':
            return {
                loading: false,
                data: action.payload,
                error: ''
            }
        case 'ERROR':
            return {
                loading: false,
                data: [],
                error: 'failed to fetch data'
            }
        default:
            return state;
    }
}

const Listings = (props) => {
    // TODO
    // This component should make a request to the api endpoint (props.dataEndpoint)
    // then render the result as set of listings as per the design mocks
    // check props passed in from parent for other values that you may need to use
    const { dataEndpoint, keyword, location } = props;
    const [state, dispatch] = useReducer(reducer, INITIAL_STATE)
    useEffect(() => {
        fetch(dataEndpoint)
            .then(response => {
                if (response.status !== 200) {
                    dispatch({ type: 'ERROR'})
                    return;
                }
                response.json().then((data) => {
                    dispatch({ type: 'SUCCESS', payload: data});
                });
            })
            .catch(error => {
                dispatch({ type: 'ERROR'})
            })
    }, []);
    const { loading, data, error } = state;
    return (
        <div className="listings">
            <div className="listings__header">
                <h2>Search Results</h2>
                {
                    !loading &&
                    <p>
                        <span>{data.length} results</span> for <span>{keyword}</span> in <span>{location}</span>
                    </p>
                }
            </div>
            <hr className="listings__rule" />
            <div className="listings__grid">
                {
                    (data.length > 0) &&
                        data.map((item, index) =><Listing key={index }{ ...item } />)
                }
            </div>
        </div>
    );
};

export default Listings;
