import React, { useEffect, useReducer } from "react";

const ACTIONS = ["View", "Reply"];

const formatAsCurrency = (int) => {
    // TODO
};

const Listing = (props) => {
    // TODO
    // This should be the component which renders an individual listing to the page

    const { title, description, imgUrl, location } = props;
    let price = props.price;
    if (typeof price === 'number') {
        price = '$' + price.toLocaleString();
    }

    return (
        <div className="listing">
            <h3>{title}</h3>
            <div className="">
                <div>{price}</div>
                <div>{location}</div>
            </div>
            <div>
                {
                    imgUrl &&
                    <img alt={title} src={imgUrl} />
                }
            </div>
            <div>
                { description &&
                    <p>
                        { description }
                    </p>
                }
            </div>
            <div>
                <button>View</button>
                <button>Reply</button>
            </div>
        </div>
    );
};

const INITIAL_STATE = {
    loading: true,
    error: '',
    data: [],
}

const reducer = (state, action) => {
    switch (action.type) {
        case 'SUCCESS':
            return {
                loading: false,
                data: action.payload,
                error: ''
            }
            break;
        case 'ERROR':
            return {
                loading: false,
                data: [],
                error: 'failed to fetch data'
            }
        default:
            return state;
    }
}

const Listings = (props) => {
    // TODO
    // This component should make a request to the api endpoint (props.dataEndpoint)
    // then render the result as set of listings as per the design mocks
    // check props passed in from parent for other values that you may need to use
    const { dataEndpoint, keyword, location } = props;
    const [state, dispatch] = useReducer(reducer, INITIAL_STATE)
    useEffect(() => {
        fetch(dataEndpoint)
            .then(response => {
                if (response.status !== 200) {
                    dispatch({ type: 'ERROR'})
                    return;
                }
                response.json().then((data) => {
                    console.log(data);
                    dispatch({ type: 'SUCCESS', payload: data});
                });
            })
            .catch(error => {
                dispatch({ type: 'ERROR'})
            })
    }, []);
    const { loading, data, error } = state;
    return (
        <div>
            <div className="listings__header">
                <h2>Search Results</h2>
                {
                    !loading &&
                    <p>
                        <span>{data.length} results</span> for <span>{keyword}</span> in <span>{location}</span>
                    </p>
                }
            </div>
            <hr />
            <div className="listings__grid">
                {
                    (data.length > 0) &&
                        data.map((item, index) =><Listing key={index }{ ...item } />)
                }
            </div>
        </div>
    );
};

export default Listings;
